import React from 'react'

import Product from "../product/product"
import PageButtonContainer from "../pageButtonsContainer/pageButtonsContainer"
import SHOP_DATA, { objProducts } from "./shop.data"

import "./products-grid.scss"



class ProductsGrid extends React.Component { 

    constructor(props) {
        super(props)

        this.state = {
            collections: SHOP_DATA,
            products: objProducts,
            page: 1
        }
    }


        //i create a new array and put every item of every collection in it

    render() {

        let arrayProducts = this.state.products

        //if a category is selected i filter the array
        if(this.props.filterProducts !== "All") {
            arrayProducts = arrayProducts.filter(product => product.category === this.props.filterProducts)           
        }

        //if a sort option is selected i sort the array
        if(this.props.sortProducts !== "0") {
            this.props.sortProducts === "+" ? 
            arrayProducts.sort((a, b) => parseFloat(b.price) - parseFloat(a.price)) 
            : arrayProducts.sort((a, b) => parseFloat(a.price) - parseFloat(b.price))
        }
        else {
            //if no sort i reverse the array because when add a new collection it comes last
            arrayProducts.reverse()
        }

        return (
            <>
                <div id="products-grid">
                    {           
                        arrayProducts.slice((this.state.page - 1), (this.state.page * 16)).map(({ id, ...itemProps }, index) => (
                            <Product key={ index } { ...itemProps } />
                        )) 
                    }                 
                </div>          
                <PageButtonContainer page={this.state.page} numResults={arrayProducts.length} />
            </>
        )
    }

    renderButtons = (page, numResults, resPerPage = 16) => {
        const pages = Math.ceil(numResults / resPerPage)
        let button

        if(page === 1 && pages > 1) {
            //first page
            button = <PageButton type= "next" page={page} changePage={(page) => this.setState({page})}/>

        } else if(page < pages) {
            //both buttons
            button = <div>
                <PageButton type= "next" page={page} changePage={(page) => this.setState({page})}/>
                <PageButton type= "prev" page={page} changePage={(page) => this.setState({page})}/>
            </div>
        }
        else if(page === pages && pages > 1) {
            //last page
            button = <PageButton type= "prev" page={page} changePage={(page) => this.setState({page})}/>
        }
        return button
    }
}

export default ProductsGrid
