import React from 'react'

const categories = ["All", "Hoodie", "Tees", "Jeans"]

export default function FilterInputs({ filterProducts, changeFilter, changeSort }) {


    return (
        <div>
            <div id="products-nav">
                {
                    categories.map((cat, i) => (
                        <span key={i} 
                        className={filterProducts === cat ? "selected" : ""} 
                        onClick={e => changeFilter(e.target.textContent)}>{cat}  {i+1 !== categories.length ? " /" : ""}
                        </span>
                    ))
                }
            </div>
            Sort by: 
            <select onChange={(e) => changeSort(e.target.value)}>
                <option value="0">Latest</option>
                <option value="-">Price: low to high</option>
                <option value="+">Price: high to low</option>
            </select>
        </div>
    )
}
