import React, { Component } from 'react'

import "./productsPage.scss"

import ProductsGrid from "../../components/products-grid/products-grid"
import Newsletter from "../../components/newsletter/newsletter"

const categories = ["All", "Hoodie", "Tees", "Pants"]

 class ProductsPage extends Component {

    constructor(props) {
        super(props)

        this.state = {
            sortProducts: null,
            filterProducts: "All"
        }
    }



    render() {
        return (
            <div>
                <div className="wrapper">
                    <div id="products-header">
                        <h2>Products</h2>
                        <div id="products-nav">
                            {
                                categories.map(cat => (
                                    <span className={this.state.sortProducts === cat ? "selected" : ""} 
                                    onClick={e => this.setState({sortProducts: cat})}>{cat}  /
                                    </span>
                                ))
                            }
                        </div>
                        <select onChange={e => this.setState({sortProducts: e.target.value})}>
                            <option value="0">Sort by latest</option>
                            <option value="-">Sort by price: low to high</option>
                            <option value="+">Sort by price: high to low</option>
                        </select>
                    </div>
                    <ProductsGrid sortBy={this.state.sortProducts} filterBy={this.state.filterProducts}/>
                </div>
                <Newsletter />
            </div>
        )
    }
}

export default ProductsPage
