import React from 'react'

export default function MenuIcon({active, onMenuClick}) {
    return (
        <span><i className="fas fa-bars" onClick={() => {
            const body = document.body.style
            if(!active) {
                body.marginLeft = "-300"

            } 
            onMenuClick()
        }}></i></span>
    )
}
