import React from 'react'

import Product from "../product/product"
import SHOP_DATA from "./shop.data"

import "./products-grid.scss"

class ProductsGrid extends React.Component { 

    constructor() {
        super()

        this.state = {
            collections: SHOP_DATA
        }
    }

    render() {
        const collections = this.state.collections.reverse()
        let arrayProducts = []
        collections.forEach(collection => collection.items.forEach(product => arrayProducts.push(product)))

        return (
            <div id="products-grid">



                { 


                    arrayProducts.sort((a, b) => parseFloat(a.price) - parseFloat(b.price)).map(({ id, ...itemProps }) => (
                        <Product key={ id } { ...itemProps } />
                    ))
                    /* arrayProducts.map(({ id, ...itemProps }) => (
                        <Product key={ id } { ...itemProps } />
                    )) */
                } 

            </div>
        )
    }
}

export default ProductsGrid
