import { ADD_CART_ITEM, REMOVE_CART_ITEM } from "../actions/types"

const cartReducer = (state = [], action) => {
    switch (action.type) {
        case ADD_CART_ITEM:
            return [
                ...state,
                action.payload
            ]



        default:
            return state
    }
}

export default cartReducer
