import { ADD_CART_ITEM, REMOVE_CART_ITEM, MODIFY_CART_ITEM } from "../actions/types"

const cartReducer = (state = [], action) => {
    const cartItem = action.payload
    const existingCartItem = state.find(item => (item.id === cartItem.id && item.size === cartItem.size))

    switch (action.type) {
        case ADD_CART_ITEM:
                if(existingCartItem){
                    return state.map(item => (
                        (item.id === cartItem.id && item.size === cartItem.size)
                        ? { ...item, quantity: item.quantity + 1 }
                        : item
                    ))
                }
                return [...state, {...cartItem, quantity: 1}]

            // case MODIFY_CART_ITEM:
