import React from 'react'

import "./plusMinusButton.scss"



export default function PlusMinusButton({ id, size, operator, modifyCartItem }) {
    const item = { id, size, operator }
    return (
        <button
        onClick={() => modifyCartItem(item)} 
        className="colorwhite" >{operator === "+" ? "+" : "-"}</button>
    )
}
