import React, { Component }  from 'react'

import "./productsInfos.scss"

export default class productInfos extends Component {
    constructor(props) {
        super(props)
        this.state = {
            size: null
        }
    }


    render() {
        const cartProps = {
            id: this.props.id,
            imageUrl: this.props.images.front,
            price: this.props.price,
            name: this.props.name,
            size: this.state.size
        }
        const sizes = ['S', 'M', 'L', 'XL']

        return (
            <div>
                {
                    sizes.map(size => (
                        <div className='radioSize'>
                            <input type="radio" name="size"
                            value={size} 
                            onClick={e => {this.setState({size: e.target.value})}}
                            disabled={this.props.disponibility[size] <= 0 ? true : false}
                            />
                            <label for={size}>{size}{this.props.disponibility[size] <= 0 ? "  out of stock": ''}</label><br/>
                        </div>
                    ))   
                }
                <button disabled={this.state.size ? false : true} className={this.state.size ? "" : "disabled"} //fare classe disabled
                onClick={() => {this.props.addCartItem(cartProps); this.props.toggleCart()}}>Add to cart</button>

                <div className='img-grid'>

                </div>
            </div>
        )
    }
}
