import { ADD_CART_ITEM, REMOVE_CART_ITEM, MODIFY_CART_ITEM } from "../actions/types"
import { addItemToCart } from "../components/shoppingCart/cart.utils"

const cartReducer = (state = [], action) => {
    switch (action.type) {
        case ADD_CART_ITEM:
                const cartItemToAdd = action.payload
                const existingCartItem = cartItems.find(item => (item.id === cartItemToAdd.id && item.size === cartItemToAdd.size))
                if(existingCartItem){
                    return cartItems.map(cartItem => (

                        (cartItem.id === cartItemToAdd.id && cartItem.size === cartItemToAdd.size)
                        ? { ...cartItem, quantity: cartItem.quantity + 1 }
                        : cartItem
                    ))
                }
                return [...cartItems, {...cartItemToAdd, quantity: 1}]

        default:
            return state  
}
}
export default cartReducer
