import React from 'react'
import PageButton from "../pageButton/pageButton"

export default function pageButtonsContainer({ page, numResults, changePage ,resPerPage = 16 }) {
        const pages = Math.ceil(numResults / resPerPage)
        let button

        if(page === 1 && pages > 1) {
            //first page
            button = <PageButton type="next" page={page} changePage/>

        } else if(page < pages) {
            //both buttons
            button = <div>
                <PageButton type="next" page={page} changePage/>
                <PageButton type="prev" page={page} changePage/>
            </div>
        }
        else if(page === pages && pages > 1) {
            //last page
            button = <PageButton type="prev" page={page} changePage/>
        }

    return (
        <div className="buttons">
            {button}
        </div>
    )
}
